import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      pokemonName: "",
      pokemonImage: ""
    };
  }

  async componentDidMount() {
    let randomNumber = Math.floor(Math.random) * 807 + 1;
    const url = "https://pokeapi.co/api/v2/pokemon/${Math.floor(Math.random) * 807}/";
    const response = await fetch(url);
    const data = await response.json();

    this.setState({ currencies: data, loading: false});
}

render() {
  if (this.state.loading) {
      return <div>loading...</div>;
    }

    if (!this.state.currencies) {
      return <div>didn't get a currency</div>;
    }

  return (
      <div>
          <CurrentDate />
          <SelectCurrency currencies={this.state.currencies} />
          <SelectRate currencies={this.state.currencies} />
      </div> 
  )
}
}

export default App;
